import React, { useEffect, useState } from 'react'
import { getUserDetails, isAuthenticated } from '../api/userAPI'

import Navbar from '../components/Navbar'
import Form from '../components/Profile_form'

const Profile = () => {
    
const {user}=isAuthenticated()
    const [Userdetail,setUserdetail]=useState({})
    useEffect(()=>{
        getUserDetails(user._id)
        .then(data=>{
            setUserdetail(data)
            console.log(data)
        })
    },[])
    
  return (
    <div>
        <Navbar/>
        <div className="container">
      <h1>User Information</h1>
      <div className="row">
        <div className="col-md-6">
          <p>Name: {Userdetail.username}</p>
          <p>Gender: {Userdetail.gender}</p>
          <p>Email: {Userdetail.email}</p>
          <p>Phone Number: {Userdetail.phone}</p>
          <p>Address: {Userdetail.location}</p>
          <p>Education:</p>
          <ul>
            {Userdetail && Userdetail.education.map((item, index) => (
              <li key={index}>
                <p>{item.school}</p>
                <p>{item.degree}</p>
                <p>
                  {item.startDate} - {item.endDate}
                </p>
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <p>Skills:</p>
          {/* <ul>
            {user && user.skills.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul> */}
          <p>Experience:</p>
          {/* <ul>
            {user && user.experience.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul> */}
        </div>
      </div>
    </div>
        {/* <button className='btn btn-success' onClick={(e)=>{
            e.preventDefault()
            if(Skill){
                showSkill(false)
            }
            else{
                showSkill(true)    
            }
            
            }}> Add Skill</button> */}
        
        {/* {
            Skill &&  */}
            
        {/* <Form/> */}
    </div>
  )
}

export default Profile
