import React, { useEffect, useState } from 'react'
import { getUserDetails, isAuthenticated } from '../api/userAPI'
import Experience from '../components/Experience'
import Navbar from '../components/Navbar'
import Form from '../components/Profile_form'

const Profile = () => {
    const [experience,showExperience]=useState(false)
    const [Education,showEducation]=useState(false)
    const [Skill,showSkill]=useState(false)
const {user}=isAuthenticated()
    const [Userdetail,setUserdetail]=useState({})
    useEffect(()=>{
        getUserDetails(user._id)
        .then(data=>{
            setUserdetail(data)
            console.log(data)
        })
    },[])
  return (
    <div>
        <Navbar/>
        <div className="container">
      <h1>User Information</h1>
      <div className="row">
        <div className="col-md-6">
          <p>Name: {user.name}</p>
          <p>Date of Birth: {user.dateOfBirth}</p>
          <p>Gender: {user.gender}</p>
          <p>Email: {user.email}</p>
          <p>Phone Number: {user.phoneNumber}</p>
          <p>Education:</p>
          <ul>
            {user && user.education.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <p>Skills:</p>
          <ul>
            {user && user.skills.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
          <p>Experience:</p>
          <ul>
            {user && user.experience.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
        {/* <button className='btn btn-success' onClick={(e)=>{
