            if(Skill){
                showSkill(false)
            }
            else{
                showSkill(true)    
            }
            
            }}> Add Skill</button> */}
        
        {/* {
            Skill &&  */}
            
        {/* <Form/> */}
    </div>
  )
}

export default Profile
