import React, { useEffect, useState } from 'react'
import { getJobDetails } from '../api/jobAPI'
import { isAuthenticated } from '../api/userAPI'
import { useParams } from 'react-router-dom'

const JobDetails = () => {

  let [jobs, setJob] = useState([])
  let { user } = isAuthenticated()
  let {id}=useParams()
  
  useEffect(() => {
    getJobDetails(user._id)
      .then((data) => {
        if (data.error) {
          console.log(data.error)

        }
        else {
          setJob(data)
        }

      })
  }, [])

  return (
    <div>
      <div class="container">
        <h1>{jobs.title}</h1>
        <p><strong>Location:</strong> {jobs.location}</p>
        <p><strong>Deadline:</strong> {jobs.deadline}</p>
        <p><strong>Experience:</strong> {jobs.experience}</p>
        <ul class="list-group">
          <li class="list-group-item"><strong>Education:</strong></li>
          {jobs.education.map((edu) => (
            <li class="list-group-item">{edu}</li>
          ))}
        </ul>
        {/* <ul class="list-group">
          <li class="list-group-item"><strong>Skills:</strong></li>
          {jobs.skills.map((skill) => (
            <li class="list-group-item">{skill}</li>
          ))}
        </ul> */}
        <p><strong>Description:</strong></p>
        <ul>
          {jobs.description.map((desc) => (
            <li>{desc}</li>
          ))}
        </ul>
      </div>


    </div>
  )
}

export default JobDetails
