import React, { useEffect, useState } from 'react'
import { getJobDetails } from '../api/jobAPI'
import { isAuthenticated } from '../api/userAPI'

const JobDetails = () => {
  
    let [jobs,setJob]=useState([])
    let {user}=isAuthenticated()
     useEffect(()=>{
      getJobDetails(user._id)
      .then((data)=>{
    if(data.error){
      console.log(data.error)
    
    }
    else{
      setJob(data)
    }
    
      })
     },[])
    
  return (
    <div>
       <div class="container">
  <h1>{jobPost.title}</h1>
  <p><strong>Location:</strong> {jobPost.location}</p>
  <p><strong>Deadline:</strong> {jobPost.deadline}</p>
  <p><strong>Experience:</strong> {jobPost.experience}</p>
  <ul class="list-group">
    <li class="list-group-item"><strong>Education:</strong></li>
    {jobPost.education.map((edu) => (
      <li class="list-group-item">{edu}</li>
    ))}
  </ul>
  <ul class="list-group">
    <li class="list-group-item"><strong>Skills:</strong></li>
    {jobPost.skills.map((skill) => (
      <li class="list-group-item">{skill}</li>
    ))}
  </ul>
  <p><strong>Description:</strong></p>
  <ul>
    {jobPost.description.map((desc) => (
      <li>{desc}</li>
    ))}
  </ul>
</div>


    </div>
  )
}

export default JobDetails
